USE registsupport_db;
SET GLOBAL sql_mode = '';
DROP TABLE IF EXISTS users;
DROP TABLE IF EXISTS credits_lists;
DROP TABLE IF EXISTS credits;
DROP TABLE IF EXISTS syllabuses;
DROP TABLE IF EXISTS timetables;
DROP TABLE IF EXISTS subjects;

CREATE TABLE users (
    user_id VARCHAR(16) PRIMARY KEY NOT NULL,
    passwords VARCHAR(255) NOT NULL,
    departments INT NOT NULL,
    grades INT NOT NULL,
    created_at DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
    updated_at DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP
) ENGINE = InnoDB
DEFAULT CHARSET = utf8
COLLATE = utf8_unicode_ci;

CREATE TABLE credits_lists (
    credit_list_id INT PRIMARY KEY NOT NULL AUTO_INCREMENT,
    user_id VARCHAR(16) NOT NULL,
    created_at DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY(user_id) REFERENCES users(user_id)
) ENGINE = InnoDB
DEFAULT CHARSET = utf8
COLLATE = utf8_unicode_ci;

CREATE TABLE credits (
    credit_id INT PRIMARY KEY NOT NULL AUTO_INCREMENT,
    credit_list_id INT NOT NULL,
    subjects_names VARCHAR(50) NOT NULL,
    evaluations VARCHAR(2) NOT NULL,
    credits VARCHAR(2) NOT NULL,
    acquisitions_years VARCHAR(5) NOT NULL,
    acquisitions_semesters VARCHAR(5) NOT NULL,
    FOREIGN KEY(credit_list_id) REFERENCES credits_lists(credit_list_id)
) ENGINE = InnoDB
DEFAULT CHARSET = utf8
COLLATE = utf8_unicode_ci;

CREATE TABLE syllabuses (
    syllabus_id VARCHAR(5) NOT NULL PRIMARY KEY,
    subjects_codes VARCHAR(10) NOT NULL,
    subjects_names VARCHAR(100) NOT NULL,
    teachers_names VARCHAR(100) NOT NULL,
    grades VARCHAR(10) NOT NULL,
    classes VARCHAR(10) NOT NULL,
    classrooms VARCHAR(20) NOT NULL,
    semesters VARCHAR(10) NOT NULL,
    day_of_the_week VARCHAR(10) NOT NULL,
    subjects_classifications VARCHAR(10) NOT NULL,
    classes_styles VARCHAR(20) NOT NULL,
    credits VARCHAR(10) NOT NULL,
    targets VARCHAR(1000) NOT NULL,
    remarks VARCHAR(500) NOT NULL,
    sublicenses VARCHAR(50)
) ENGINE = InnoDB
DEFAULT CHARSET = utf8
COLLATE = utf8_unicode_ci;

CREATE TABLE timetables (
    timetable_id INT PRIMARY KEY NOT NULL AUTO_INCREMENT,
    user_id VARCHAR(16) NOT NULL,
    created_at DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY(user_id) REFERENCES users(user_id)
) ENGINE = InnoDB
DEFAULT CHARSET = utf8
COLLATE = utf8_unicode_ci;

CREATE TABLE subjects (
    subject_id INT NOT NULL PRIMARY KEY AUTO_INCREMENT,
    timetable_id INT NOT NULL,
    syllabus_id VARCHAR(5) NOT NULL,
    subjects_codes VARCHAR(10) NOT NULL,
    subjects_names VARCHAR(100) NOT NULL,
    teachers_names VARCHAR(100) NOT NULL,
    grades VARCHAR(10) NOT NULL,
    classes VARCHAR(10) NOT NULL,
    classrooms VARCHAR(20) NOT NULL,
    semesters VARCHAR(10) NOT NULL,
    day_of_the_week VARCHAR(10) NOT NULL,
    subjects_classifications VARCHAR(10) NOT NULL,
    classes_styles VARCHAR(20) NOT NULL,
    credits VARCHAR(10) NOT NULL,
    targets VARCHAR(1000) NOT NULL,
    remarks VARCHAR(500) NOT NULL,
    sublicenses VARCHAR(50),
    FOREIGN KEY(timetable_id) REFERENCES timetables(timetable_id),
    FOREIGN KEY(syllabus_id) REFERENCES syllabuses(syllabus_id)
) ENGINE = InnoDB
DEFAULT CHARSET = utf8
COLLATE = utf8_unicode_ci;


